mapper.insert:
-- post_images 테이블에 데이터 추가
        INSERT INTO post_images (post_idx, post_image_path, post_t_image_path)
        VALUES (#{postIdx}, #{postImagePath}, #{postTImagePath});

mapper.update:
update posts
set post_visitcount = post_visitcount + 1
where post_idx = #{postIdx}

update posts
set post_title = #{postTitle}, name = #{name},
    post_content = #{postContent}
where post_idx = #{postIdx}
